var params = $input.first().json;

var lookup = {
  'Environmental': 'ENV-001',
  'Water':         'WCN-001',
  'Planning':      'APP-001',
  'Electricity':   'NEC-001',
  'Effluent':      'TEF-001'
};

var formType = params.form_type;
var ref = lookup[formType];

if (!ref) {
  throw new Error('Unknown form_type: ' + formType + '. Valid values: ' + Object.keys(lookup).join(', '));
}

return [{
  json: {
    run_id: 'Demo-' + ref + '-' + new Date().toISOString().slice(0, 16).replace(/[T:]/g, '-'),
    form_type: formType,
    template_ref: ref,
    user_id: params.user_id,
    additional_data: params.additional_data || {}
  }
}];

var result = $input.first().json;
var params = $('Resolve Template').first().json;

return [{
  json: {
    demo: 'Form Fill Demo',
    form_type: params.form_type,
    template_ref: params.template_ref,
    run_id: params.run_id,
    status: result.status || 'unknown',
    fields_filled: result.fields_filled || 0,
    fields_missing: result.fields_missing || 0,
    final_pdf: result.final_pdf || '',
    warnings: result.warnings || []
  }
}];
